contracts/ContentInfo.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

contract ContentInfo {
    struct Content {
        uint256 contentId; // 文章id
        uint256 userId; // 用户id
        string title; // 标题
        string content; // 内容
        uint256 updateTime; // 更新时间
        uint256 praiseCount; // 点赞数
        uint256 contentState; //文章状态  1存在 0删除
    }

    struct Comment {
        uint256 contentId; // 文章ID
        uint256 userId; // 用户ID
        uint256 commentId; //评论ID
        string comment; // 评论内容
        uint256 updateTime; // 评论发表时间
        uint256 praiseCount; // 赞的数
        uint256 commentState; // 评论状态  1存在 0删除
    }

    struct Complain {
        uint256 complaintId; // 投诉id
        uint256 contentId; // 文章ID
        uint256 userId; // 用户ID
        uint256 commentId; // 评论ID
        string content; // 投诉内容
        uint256 updateTime; // 投诉发表时间
        uint256 complainState; // 评论状态  1存在 0删除
    }

    struct ReturnContent {
        string contentId; // 文章id
        string userId; // 用户id
        string title; // 标题
        string content; // 内容
        string updateTime; // 更新时间
        string praiseCount; // 点赞数
        string contentState; //文章状态
    }

    struct ReturnComment {
        string contentId; // 文章id
        string userId; // 用户id
        string commentId; // 评论id
        string comment; // 评论内容
        string updateTime; // 更新时间
        string praiseCount; // 点赞数
        string commentState; // 评论状态
    }

    struct ReturnComplain {
        string complaintId; // 投诉id
        string contentId; // 文章ID
        string userId; // 用户ID
        string commentId; // 评论ID
        string content; // 投诉内容
        string updateTime; // 投诉发表时间
        string complainState; // 投诉状态
    }

    mapping(uint256 => Content) public contentMap;
    uint256[] public contentIds;
    uint256[] public complainIds;
    mapping(uint256 => Comment) public commentMap;
    mapping(uint256 => Complain) public complainMap;
    mapping(uint256 => uint256[]) public contentComments; // 映射 contentId 到评论数组

    // event UserRegistered(string userId);
    event ContentCreated(uint256 contentId);
    event PraiseDone(uint256 praiseCount); //这是对帖子进行评论的内容
    event CommentCreated(uint256 commentId);
    event PraiseCommentDone(uint256 praiseCount);
    event ComplaintCreated(uint256 complaintId);

    function createContent(
        uint256 _userId,
        string memory _title,
        string memory _content
    ) public {
        uint256 _contentId = uint256(
            keccak256(abi.encodePacked(block.timestamp, _userId, _title))
        );
        contentMap[_contentId] = Content(
            _contentId,
            _userId,
            _title,
            _content,
            block.timestamp,
            0,
            1
        );
        contentIds.push(_contentId);
        emit ContentCreated(_contentId);
    }

    function getContent(
        uint256 _contentId
    ) public view returns (ReturnContent memory) {
        Content memory content = contentMap[_contentId];
        ReturnContent memory returnContent = ReturnContent(
            uint256ToString(content.contentId),
            uint256ToString(content.userId),
            content.title,
            content.content,
            uint256ToString(content.updateTime),
            uint256ToString(content.praiseCount),
            uint256ToString(content.contentState)
        );
        return returnContent;
    }

    function updateContent(
        uint256 _contentId,
        string memory _title,
        string memory _content
    ) public {
        Content memory contentOld = contentMap[_contentId];
        contentOld.title = _title;
        contentOld.content = _content;
        contentOld.updateTime = block.timestamp;
        contentMap[_contentId] = contentOld;
    }

    function deleteContent(uint256 _contentId) public {
        Content memory contentDelete = contentMap[_contentId];
        contentDelete.contentState = 0;
        contentMap[_contentId] = contentDelete;
    }

    function praiseContent(uint256 _contentId) public {
        contentMap[_contentId].praiseCount++;
        emit PraiseDone(contentMap[_contentId].praiseCount);
    }

    function getAllContent() public view returns (ReturnContent[] memory) {
        uint256 count = contentIds.length;
        uint256 existCount = 0;
        for (uint256 i = 0; i < count; i++) {
            if (contentMap[contentIds[i]].contentState == 1) {
                existCount++;
            }
        }
        ReturnContent[] memory allContent = new ReturnContent[](existCount);
        uint256 index = 0;
        for (uint256 i = 0; i < count; i++) {
            if (contentMap[contentIds[i]].contentState == 1) {
                allContent[index] = getContent(contentIds[i]);
                index++;
            }
        }
        return allContent;
    }

    //创建评论
    function createComment(
        uint256 _contentId,
        uint256 _userId,
        string memory _comment
    ) public {
        //记得最后开启
        // require(
        //     contentMap[_contentId].contentState == 1,
        //     "Content does not exist"
        // );

        // Generate comment ID
        uint256 _commentId = uint256(
            keccak256(abi.encodePacked(block.timestamp, _contentId, _userId))
        );

        // Create the comment struct
        Comment memory comment = Comment(
            _contentId,
            _userId,
            _commentId,
            _comment,
            block.timestamp,
            0, // Initial praise count is 0
            1 // Comment state is active
        );
        commentMap[_commentId] = comment;
        // // Update the content's comment count
        contentComments[_contentId].push(_commentId);
        // Trigger an event
        emit CommentCreated(_commentId);
    }

    // 更新评论
    function updateComment(uint256 _commentId, string memory _comment) public {
        // 确保评论存在
        // require(
        //     commentMap[_commentId].commentState == 1,
        //     "Comment does not exist"
        // );

        // 更新评论内容
        Comment memory comment = commentMap[_commentId];
        comment.comment = _comment;
        comment.updateTime = block.timestamp;
        commentMap[_commentId] = comment;
    }

    // 获取评论
    function getComment(
        uint256 _commentId
    ) public view returns (ReturnComment memory) {
        // 确保评论存在
        // require(
        //     commentMap[_commentId].commentState == 1,
        //     "Comment does not exist"
        // );
        Comment memory comment = commentMap[_commentId];
        ReturnComment memory returnComment = ReturnComment(
            uint256ToString(comment.contentId),
            uint256ToString(comment.userId),
            uint256ToString(comment.commentId),
            comment.comment,
            uint256ToString(comment.updateTime),
            uint256ToString(comment.praiseCount),
            uint256ToString(comment.commentState)
        );
        // 返回评论内容
        return returnComment;
    }

    // 删除评论
    function deleteComment(uint256 _commentId) public {
        // 确保评论存在
        // require(
        //     commentMap[_commentId].commentState == 1,
        //     "Comment does not exist"
        // );

        // 删除评论
        Comment memory commentDelete = commentMap[_commentId];
        commentDelete.commentState = 0;
        commentMap[_commentId] = commentDelete;
    }

    function getCommentsByContentId(
        uint256 _contentId
    ) public view returns (ReturnComment[] memory) {
        //contentId对应的评论的数组
        uint256[] memory commmentArray = contentComments[_contentId];
        uint256 count = commmentArray.length;
        uint256 existCount = 0;
        // 遍历所有评论，统计符合条件的评论数量
        for (uint256 i = 0; i < count; i++) {
            if (commentMap[commmentArray[i]].commentState == 1) {
                existCount++;
            }
        }
        // 创建一个数组用于存储符合条件的评论
        ReturnComment[] memory comments = new ReturnComment[](existCount);
        uint256 index = 0;
        // 遍历所有评论，将符合条件的评论存入数组
        for (uint256 i = 0; i < count; i++) {
            if (commentMap[commmentArray[i]].commentState == 1) {
                comments[index] = getComment(commmentArray[i]);
                index++;
            }
        }
        // 返回所有评论
        return comments;
    }

    function praiseComment(uint256 _commentId) public {
        // 确保评论存在
        // require(
        //     commentMap[_commentId].commentState == 1,
        //     "Comment does not exist"
        // );

        // 增加评论的赞数
        // Comment memory comment = commentMap[_commentId];
        // comment.praiseCount++;
        commentMap[_commentId].praiseCount++;
        emit PraiseCommentDone(commentMap[_commentId].praiseCount);
        // commentMap[_commentId] = comment;
        // 触发事件
    }

    function createComplaint(
        uint256 _contentId,
        uint256 _userId,
        uint256 _commentId,
        string memory _complaintContent
    ) public {
        uint256 _complaintId = uint256(
            keccak256(
                abi.encodePacked(
                    block.timestamp,
                    _contentId,
                    _userId,
                    _commentId
                )
            )
        );
        complainMap[_complaintId] = Complain(
            _complaintId,
            _contentId,
            _userId,
            _commentId,
            _complaintContent,
            block.timestamp,
            1 // 投诉状态为存在
        );
        complainIds.push(_complaintId);
        emit ComplaintCreated(_complaintId);
    }

    function getComplain(
        uint256 _complaintId
    ) public view returns (ReturnComplain memory) {
        // 确保投诉存在
        // require(
        //     complainMap[_complaintId].complainState == 1,
        //     "Complain does not exist"
        // );

        Complain memory complain = complainMap[_complaintId];
        ReturnComplain memory returnComplain = ReturnComplain(
            uint256ToString(complain.complaintId),
            uint256ToString(complain.contentId),
            uint256ToString(complain.userId),
            uint256ToString(complain.commentId),
            complain.content,
            uint256ToString(complain.updateTime),
            uint256ToString(complain.complainState)
        );
        // 返回投诉内容
        return returnComplain;
    }

    // 删除投诉
    function deleteComplain(uint256 _complainId) public {
        // 确保投诉存在
        // require(
        //     complainMap[_complainId].complainState == 1,
        //     "Complain does not exist"
        // );

        // 删除投诉
        Complain memory complainDelete = complainMap[_complainId];
        complainDelete.complainState = 0;
        complainMap[_complainId] = complainDelete;
    }

    // 获取被投诉的内容
    function getAllComplain() public view returns (ReturnComplain[] memory) {
        uint256 count = complainIds.length;
        uint256 complainedCount = 0;
        for (uint256 i = 0; i < count; i++) {
            if (complainMap[complainIds[i]].complainState == 1) {
                complainedCount++;
            }
        }
        ReturnComplain[] memory complainedContent = new ReturnComplain[](
            complainedCount
        );
        uint256 index = 0;
        for (uint256 i = 0; i < count; i++) {
            if (complainMap[complainIds[i]].complainState == 1) {
                complainedContent[index] = getComplain(complainIds[i]);
                index++;
            }
        }
        return complainedContent;
    }

    // 将 uint256 类型转换为字符串
    function uint256ToString(
        uint256 value
    ) public pure returns (string memory) {
        if (value == 0) {
            return "0";
        }
        uint256 temp = value;
        uint256 digits;
        while (temp != 0) {
            digits++;
            temp /= 10;
        }
        bytes memory buffer = new bytes(digits);
        while (value != 0) {
            digits -= 1;
            buffer[digits] = bytes1(uint8(48 + (value % 10)));
            value /= 10;
        }
        return string(buffer);
    }
}

contracts/ManageInfo.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

import "./UserInfo.sol";
import "./ContentInfo.sol";
import "./TokenERC20.sol";

contract ManageInfo {
    UserInfo public userInfo;
    ContentInfo public contentInfo;
    TokenERC20 public tokenInfo;

    // 委员会成员投票结构体
    struct Vote {
        uint256 voter; // 投票者地址
        uint256 complaintId; // 投诉的ID
        uint256 inSupport; // 是否支持投诉 "1"是支持 "0"是不支持"
    }
    //委员会成员
    uint256[] commitMember;
    // 投票数组
    mapping(uint256 => Vote[]) complainVotesMap;

    constructor(
        address _userInfoAddress,
        address _contentInfoAddress,
        address _tokenInfoAddress
    ) {
        userInfo = UserInfo(_userInfoAddress);
        contentInfo = ContentInfo(_contentInfoAddress);
        tokenInfo = TokenERC20(_tokenInfoAddress);
    }

    function getTopAssetUsers(uint256 count) public returns (uint256[] memory) {
        uint256[] memory registeredUserId = userInfo.getRegisteredUserId();
        uint256[] memory assetAmounts = new uint256[](registeredUserId.length);

        for (uint256 i = 0; i < registeredUserId.length; i++) {
            assetAmounts[i] = tokenInfo.getBalance(
                userInfo.getUserInfo(registeredUserId[i]).userAddress
            );
        }

        // 使用大顶堆找到前11名用户
        uint256[] memory topUsers = new uint256[](count);
        uint256[] memory topAssets = new uint256[](count);

        // 建立大顶堆
        for (uint256 i = 0; i < count && i < registeredUserId.length; i++) {
            topUsers[i] = registeredUserId[i];
            topAssets[i] = assetAmounts[i];
            heapify(topAssets, topUsers, i);
        }

        // 继续迭代剩余的用户
        for (uint256 i = count; i < registeredUserId.length; i++) {
            if (assetAmounts[i] > topAssets[0]) {
                topAssets[0] = assetAmounts[i];
                topUsers[0] = registeredUserId[i];
                heapify(topAssets, topUsers, 0);
            }
        }

        return topUsers;
    }

    // 用于调整堆
    function heapify(
        uint256[] memory assets,
        uint256[] memory users,
        uint256 root
    ) internal {
        uint256 largest = root;
        uint256 left = 2 * root + 1;
        uint256 right = 2 * root + 2;

        if (left < assets.length && assets[left] > assets[largest]) {
            largest = left;
        }

        if (right < assets.length && assets[right] > assets[largest]) {
            largest = right;
        }

        if (largest != root) {
            (assets[root], assets[largest]) = (assets[largest], assets[root]);
            (users[root], users[largest]) = (users[largest], users[root]);
            heapify(assets, users, largest);
        }
    }

    function getCommitMembers() public returns (string[] memory) {
        uint256[] memory topAssetUsers = getTopAssetUsers(3);
        string[] memory ret = new string[](3);
        // uint256[] memory registeredUser = userInfo.getRegisteredUserId();

        // // 随机抽取9位用户
        // uint256[] memory randomUsers = getRandomIds(registeredUser);

        // 将资产排名前11的用户和随机抽取的9位用户的 userRole 改为委员会成员
        for (uint256 i = 0; i < topAssetUsers.length; i++) {
            userInfo.changeUserRole(topAssetUsers[i], "1");
            ret[i] = uint256ToString(topAssetUsers[i]);
        }
        // for (uint256 i = 0; i < randomUsers.length; i++) {
        //     userInfo.changeUserRole(randomUsers[i], "Committee Member");
        // }
        commitMember = topAssetUsers;
        // 返回委员会成员
        return ret;
    }

    function returnCommitMembers() public view returns (string[] memory) {
        string[] memory ret = new string[](3);
        // uint256[] memory registeredUser = userInfo.getRegisteredUserId();

        // // 随机抽取9位用户
        // uint256[] memory randomUsers = getRandomIds(registeredUser);

        // 将资产排名前11的用户和随机抽取的9位用户的 userRole 改为委员会成员
        for (uint256 i = 0; i < commitMember.length; i++) {
            ret[i] = uint256ToString(commitMember[i]);
        }
        // for (uint256 i = 0; i < randomUsers.length; i++) {
        //     userInfo.changeUserRole(randomUsers[i], "Committee Member");
        // }
        return ret;
    }

    // 辅助函数：从注册用户中随机抽取不重复的九个地址
    function getRandomIds(
        uint256[] memory registeredUserAddresses
    ) internal view returns (uint256[] memory) {
        require(
            registeredUserAddresses.length >= 9,
            "Insufficient registered users"
        );

        uint256[] memory randomAddresses = new uint256[](9);
        bool[] memory selectedIndices = new bool[](
            registeredUserAddresses.length
        );
        uint256 remainingUsers = registeredUserAddresses.length;
        uint256 remainingSelections = 9;

        // 使用伪随机数生成器选择随机用户地址
        while (remainingSelections > 0) {
            uint256 randomIndex = uint256(
                keccak256(
                    abi.encodePacked(
                        block.timestamp,
                        block.difficulty,
                        block.number
                    )
                )
            ) % remainingUsers;

            // 如果选择的索引没有被选中过，则将其添加到随机地址数组中，并标记为已选中
            if (!selectedIndices[randomIndex]) {
                randomAddresses[
                    9 - remainingSelections
                ] = registeredUserAddresses[randomIndex];
                selectedIndices[randomIndex] = true;
                remainingSelections--;
            }

            // 将已选中的地址移至末尾，避免重复选择
            if (randomIndex != remainingUsers - 1) {
                registeredUserAddresses[randomIndex] = registeredUserAddresses[
                    remainingUsers - 1
                ];
            }

            // 缩小可选范围
            remainingUsers--;
        }

        return randomAddresses;
    }

    function isCommitteeMember(uint256 userId) public view returns (bool) {
        for (uint256 i = 0; i < commitMember.length; i++) {
            if (commitMember[i] == userId) return true;
        }
        return false;
    }

    // 进行投票
    function makeVote(
        uint256 userId,
        uint256 complainId,
        uint256 isVote
    ) public {
        // // 检查是否为委员会成员
        // require(
        //        isCommitteeMember(userId),
        //     "Only committee members can vote"
        // );

        // 检查用户是否已经投过票
        // require(
        //     !hasVoted(userId, complainId),
        //     "You have already voted for this complaint"
        // );

        // 创建投票对象并添加到投票数组中
        Vote memory newVote = Vote({
            voter: userId,
            complaintId: complainId,
            inSupport: isVote
        });
        complainVotesMap[complainId].push(newVote);
    }

    // 删除文章
    function deleteContent(uint256 contentId) public {
        // 在 ContentInfo 合约中定义了删除文章的函数，可以调用相关函数来删除
        contentInfo.deleteContent(contentId);
    }

    // 删除评论
    function deleteComment(uint256 commentId) public {
        // 在 ContentInfo 合约中定义了删除评论的函数，可以调用相关函数来删除
        contentInfo.deleteComment(commentId);
    }

    // 删除评论
    function deleteComplain(uint256 complainId) public {
        // 在 ContentInfo 合约中定义了删除评论的函数，可以调用相关函数来删除
        contentInfo.deleteComplain(complainId);
    }

    // 获取投票结果
    function getVoteResults(
        uint256 _complaintId
    ) public view returns (string memory) {
        uint256 supportCount = 0;
        uint256 oppositionCount = 0;
        Vote[] memory votes = complainVotesMap[_complaintId];
        for (uint256 i = 0; i < votes.length; i++) {
            if (votes[i].inSupport == 1) {
                supportCount++;
            } else {
                oppositionCount++;
            }
        }
        return uint256ToString(supportCount);
    }

    // 检查用户是否已经投过票
    function hasVoted(
        uint256 _voter,
        uint256 _complaintId
    ) internal view returns (bool) {
        Vote[] memory votes = complainVotesMap[_complaintId];
        for (uint256 i = 0; i < votes.length; i++) {
            if (votes[i].voter == _voter) {
                return true;
            }
        }
        return false;
    }

    // 将 uint256 类型转换为字符串
    function uint256ToString(
        uint256 value
    ) public pure returns (string memory) {
        if (value == 0) {
            return "0";
        }
        uint256 temp = value;
        uint256 digits;
        while (temp != 0) {
            digits++;
            temp /= 10;
        }
        bytes memory buffer = new bytes(digits);
        while (value != 0) {
            digits -= 1;
            buffer[digits] = bytes1(uint8(48 + (value % 10)));
            value /= 10;
        }
        return string(buffer);
    }
}

contracts/TokenERC20.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

interface tokenRecipient {
    function receiveApproval(
        address _from,
        uint256 _value,
        address _token,
        bytes calldata _extraData
    ) external;
}

contract TokenERC20 {
    // Public variables of the token
    string public name;
    string public symbol;
    uint8 public decimals = 18;
    // 18 decimals is the strongly suggested default, avoid changing it
    uint256 public totalSupply;

    // This creates an array with all balances
    mapping(address => uint256) public balanceOf;
    mapping(address => mapping(address => uint256)) public allowance;

    // This generates a public event on the blockchain that will notify clients
    event Transfer(address indexed from, address indexed to, uint256 value);

    // This generates a public event on the blockchain that will notify clients
    event Approval(
        address indexed _owner,
        address indexed _spender,
        uint256 _value
    );

    // This notifies clients about the amount burnt
    event Burn(address indexed from, uint256 value);

    /**
     * Constructor function
     *
     * Initializes contract with initial supply tokens to the creator of the contract
     */
    constructor(
        uint256 initialSupply,
        string memory tokenName,
        string memory tokenSymbol
    ) {
        totalSupply = initialSupply * 10 ** uint256(decimals); // Update total supply with the decimal amount
        balanceOf[msg.sender] = totalSupply; // Give the creator all initial tokens
        name = tokenName; // Set the name for display purposes
        symbol = tokenSymbol; // Set the symbol for display purposes
    }

    /**
     * Internal transfer, only can be called by this contract
     */
    function _transfer(address _from, address _to, uint256 _value) internal {
        // Prevent transfer to 0x0 address. Use burn() instead
        require(_to != address(0x0));
        // Check if the sender has enough
        require(balanceOf[_from] >= _value);
        // Check for overflows
        require(balanceOf[_to] + _value >= balanceOf[_to]);
        // Save this for an assertion in the future
        uint256 previousBalances = balanceOf[_from] + balanceOf[_to];
        // Subtract from the sender
        balanceOf[_from] -= _value;
        // Add the same to the recipient
        balanceOf[_to] += _value;
        emit Transfer(_from, _to, _value);
        // Asserts are used to use static analysis to find bugs in your code. They should never fail
        assert(balanceOf[_from] + balanceOf[_to] == previousBalances);
    }

    /**
     * Transfer tokens
     *
     * Send `_value` tokens to `_to` from your account
     *
     * @param _to The address of the recipient
     * @param _value the amount to send
     */
    function transfer(
        address _to,
        uint256 _value
    ) public returns (bool success) {
        _transfer(msg.sender, _to, _value);
        return true;
    }

    /**
     * Transfer tokens from other address
     *
     * Send `_value` tokens to `_to` on behalf of `_from`
     *
     * @param _from The address of the sender
     * @param _to The address of the recipient
     * @param _value the amount to send
     */
    function transferFrom(
        address _from,
        address _to,
        uint256 _value
    ) public returns (bool success) {
        require(_value <= allowance[_from][msg.sender]); // Check allowance
        allowance[_from][msg.sender] -= _value;
        _transfer(_from, _to, _value);
        return true;
    }

    /**
     * Set allowance for other address
     *
     * Allows `_spender` to spend no more than `_value` tokens on your behalf
     *
     * @param _spender The address authorized to spend
     * @param _value the max amount they can spend
     */
    function approve(
        address _spender,
        uint256 _value
    ) public returns (bool success) {
        allowance[msg.sender][_spender] = _value;
        emit Approval(msg.sender, _spender, _value);
        return true;
    }

    /**
     * Set allowance for other address and notify
     *
     * Allows `_spender` to spend no more than `_value` tokens on your behalf, and then ping the contract about it
     *
     * @param _spender The address authorized to spend
     * @param _value the max amount they can spend
     * @param _extraData some extra information to send to the approved contract
     */
    function approveAndCall(
        address _spender,
        uint256 _value,
        bytes memory _extraData
    ) public returns (bool success) {
        tokenRecipient spender = tokenRecipient(_spender);
        if (approve(_spender, _value)) {
            spender.receiveApproval(
                msg.sender,
                _value,
                address(this),
                _extraData
            );
            return true;
        }
    }

    /**
     * Destroy tokens
     *
     * Remove `_value` tokens from the system irreversibly
     *
     * @param _value the amount of money to burn
     */
    function burn(uint256 _value) public returns (bool success) {
        require(balanceOf[msg.sender] >= _value); // Check if the sender has enough
        balanceOf[msg.sender] -= _value; // Subtract from the sender
        totalSupply -= _value; // Updates totalSupply
        emit Burn(msg.sender, _value);
        return true;
    }

    /**
     * Destroy tokens from other account
     *
     * Remove `_value` tokens from the system irreversibly on behalf of `_from`.
     *
     * @param _from the address of the sender
     * @param _value the amount of money to burn
     */
    function burnFrom(
        address _from,
        uint256 _value
    ) public returns (bool success) {
        require(balanceOf[_from] >= _value); // Check if the targeted balance is enough
        require(_value <= allowance[_from][msg.sender]); // Check allowance
        balanceOf[_from] -= _value; // Subtract from the targeted balance
        allowance[_from][msg.sender] -= _value; // Subtract from the sender's allowance
        totalSupply -= _value; // Update totalSupply
        emit Burn(_from, _value);
        return true;
    }

    function getBalance(address userAddress) public view returns (uint256) {
        return balanceOf[userAddress];
    }
}

contracts/UserInfo.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

contract UserInfo {
    // 定义用户结构体
    struct User {
        address userAddress; // 用户钱包地址
        uint256 userId; // 用户ID
        string userName; // 用户名
        string pwd; // 密码
        string role; // 用户角色
        uint256 assert; // 用户资产
        uint256 userState; //用户状态 1存在 0删除
    }

    struct ReturnUser {
        address userAddress; // 用户钱包地址
        string userId; // 用户ID
        string userName; // 用户名
        string pwd; // 密码
        string role; // 用户角色
        string assert; // 用户资产
        string userState; //用户状态
    }
    // 用户ID到用户结构体的映射
    mapping(uint256 => User) public userMap;
    // event UserRegistered(string userId);
    event UserRegistered(uint256 userId);

    // 已注册用
    uint256[] registeredUserId;

    // 添加用户函数
    function createUser(
        string memory _userName,
        string memory _pwd,
        address _userAddress //
    ) public {
        // 生成用户ID（使用简单的伪随机数生成函数）
        uint _userId = uint256(
            keccak256(abi.encodePacked(block.timestamp, _userName))
        );
        // string memory _userId = uint256ToString(userId);
        // 触发事件
        User memory newUser = User(
            _userAddress,
            _userId,
            _userName,
            _pwd,
            // 普通用户roleID是“0”
            "0",
            0,
            1
        );
        userMap[_userId] = newUser;
        registeredUserId.push(_userId);
        emit UserRegistered(_userId);
    }

    // 用户登录函数
    function loginUser(
        uint256 _userId,
        string memory _pwd
    ) public view returns (bool) {
        return
            keccak256(abi.encodePacked(userMap[_userId].pwd)) ==
            keccak256(abi.encodePacked(_pwd));
    }

    // 查询用户信息函数
    function getUserInfo(
        uint256 _userId
    ) public view returns (ReturnUser memory) {
        User memory user = userMap[_userId];
        ReturnUser memory returnUser = ReturnUser(
            user.userAddress,
            uint256ToString(user.userId),
            user.userName,
            user.pwd,
            user.role,
            uint256ToString(user.assert),
            uint256ToString(user.userState)
        );
        return returnUser;
    }

    // 更改用户角色函数
    function changeUserRole(uint256 _userId, string memory _role) public {
        userMap[_userId].role = _role;
    }

    function updateUserInfo(
        address userAddress, // 用户钱包地址
        uint256 userId, // 用户ID
        string memory userName, // 用户名
        string memory pwd, // 密码
        string memory role,
        uint256 assertUpdate
    ) public {
        User memory newUser = User(
            userAddress,
            userId,
            userName,
            pwd,
            // 普通用户roleID是“0”
            role,
            assertUpdate,
            userMap[userId].userState
        );
        userMap[userId] = newUser;
    }

    function getRegisteredUserId() public view returns (uint256[] memory) {
        return registeredUserId;
    }

    // 将 uint256 类型转换为字符串
    function uint256ToString(
        uint256 value
    ) public pure returns (string memory) {
        if (value == 0) {
            return "0";
        }
        uint256 temp = value;
        uint256 digits;
        while (temp != 0) {
            digits++;
            temp /= 10;
        }
        bytes memory buffer = new bytes(digits);
        while (value != 0) {
            digits -= 1;
            buffer[digits] = bytes1(uint8(48 + (value % 10)));
            value /= 10;
        }
        return string(buffer);
    }
}

